import React from "react";
class App extends React.Component {

    state = {
        backStake: "10.00",
        totalBackOdds: "",
        acca: [
            {
                name: "leg1",
                backOdds: "1.56",
                layOdds: "1.55",
                commision: "2"
            }
        ],
        legCount: 1
    };

    handleInputChange = (event) => {
        const totalBackOdds = this.state.acca.reduce((total, leg) => {
            return leg.backOdds * total
            }, 1);
        const legName = event.currentTarget.parentNode.getAttribute("name");
        const legInput = event.currentTarget.getAttribute("name");
        const value = event.currentTarget.value;
        if (legInput === "backStake") {
            this.setState({
                backStake: value,
                totalBackOdds
            });
        } else {
            this.setState(prevState => ({
                acca: prevState.acca.map(
                leg => {
                    if (leg.name === legName) {
                        return Object.assign(leg, { [legInput]: value })
                    } else {
                        return leg
                    }
                })
            }));
        }
    }

    handleAddLeg = () => {
        const legCount = this.state.legCount;
        const name = `leg${legCount + 1}`;
        const leg = {name, backOdds: "", layOdds: "", commision: ""};
        this.setState(prevState => ({
            acca: prevState.acca.concat({...leg}),
            legCount: prevState.legCount + 1
            })
        );
    }

    handleRemoveLeg = (event) => {
        const legName = event.currentTarget.parentNode.getAttribute("name");
        const legIndex = this.state.acca.findIndex(leg => 
            leg.name === legName);
        this.setState(prevState => ({
            acca: [
                ...prevState.acca.slice(0, legIndex),
                ...prevState.acca.slice(legIndex + 1)
            ]
        }));
    }

    render() {
        let listLegs = this.state.acca
            .map(leg =>
                <LegInputs
                    key={leg.name}
                    name={leg.name}
                    backOdds={leg.backOdds}
                    layOdds={leg.layOdds}
                    commision={leg.commision}
                    handleChange={this.handleInputChange}
                    removeLeg={this.handleRemoveLeg}
                />
        );

        return (
            <div>
                <input
                    name="backStake"
                    value={this.state.backStake}
                    onChange={this.handleInputChange}
                />
                <input
                    name="totalBackOdds"
                    value={this.state.totalBackOdds}
                />
                {listLegs}
                <button onClick={this.handleAddLeg}>Add leg</button>
            </div>
        );
    }
}

function LegInputs(props) {
    return (
        <div name={props.name}>
            <input type="text" value={props.backOdds}
                name="backOdds" onChange={props.handleChange} />
            <input type="text" value={props.layOdds}
                name="layOdds" onChange={props.handleChange} />
            <input type="text" value={props.commision}
                name="commision" onChange={props.handleChange} />
            {props.name !== "leg1" && 
                <button onClick={props.removeLeg}>Remove leg</button>}
        </div>
    );
}

export default App;
