import React from "react";
import Header from "./Header";
import Navigation from "./Navigation";
import Tools from "./Tools";
import Welcome from "./Welcome";
import Calculator from "./Calculator";
import Acca from "./Acca";
import { Switch, Route } from "react-router-dom";
import "./App.css";

class App extends React.Component {
    state = {
        betType: "normal",
        backStake: "",
        backOdds: "",
        layOdds: "",
        layCommision: "",
        navigationOpen: false
    }



    handleNavigation = () => {
        this.setState(prevState => ({
            navigationOpen: !prevState.navigationOpen
        }));
    }

    handleCloseNavigation = () => {
        this.setState({
            navigationOpen: false
        });
    }

    handleInputChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    }

    render() {
        const pushedMainStyle = `${this.state.navigationOpen ? "Pushed" : ""}`;
        return (
            <div className="App">
                <Header handleNavigation={this.handleNavigation}/>
                <Navigation
                    navigationOpen={this.state.navigationOpen}
                    handleClick={this.handleCloseNavigation}
                />
                <main className={`Main-Content ${pushedMainStyle}`}
                    onClick={this.handleCloseNavigation}>
                    <Switch>
                        <Route exact path="/" render={ () => (Welcome) } />
                        <Route path="/tools" component={Tools} />
                    </Switch>
                </main>
            </div>
        )
    }
}

export default App;

