function accaLayStakes(prevState, lastLegLay) {
	let acca = prevState.acca;
	for (let i = acca.length - 1; i >= 0; i--) {
		if (acca[i] == acca[acca.length - 1]) {
			Object.assign(acca[i], {lstake: lastLegLay})
		} else {
			Object.assign(acca[i], {lstake:
				Math.floor(acca[i + 1].lstake * (1 - (acca[i + 1].comm / 100)) /
				(acca[i].lodds - (acca[i].comm / 100)) * 100) / 100
			})
		}
	}
}

export default accaLayStakes;
