import React from "react";
class App extends React.Component {

    state = {
        backStake: "10.00",
        acca: [
            {
                name: "leg1",
                backOdds: "1.56",
                layOdds: "1.55",
                commision: "2"
            }
        ],
        legCount: 1
    };

    handleInputChange = (event) => {
        const legName = event.currentTarget.parentNode.getAttribute("name");
        const legInput = event.currentTarget.getAttribute("name");
        const value = event.currentTarget.value;
        this.setState(prevState => ({
            acca: prevState.acca.map(
            leg => {
                if (leg.name === legName) {
                    return Object.assign(leg, { [legInput]: value })
                } else {
                    return leg
                }
            })
        }));
    }

    handleAddLegClick = () => {
        const legCount = this.state.legCount;
        const name = `leg${legCount + 1}`;
        const leg = {name, backOdds: "", layOdds: "", commision: ""};
        this.setState(prevState => ({
            acca: prevState.acca.concat({...leg}),
            legCount: prevState.legCount + 1
            })
        );
    }

    handleRemoveLegClick = (event) => {
        const legName = event.currentTarget.parentNode.getAttribute("name");
        const legIndex = this.state.acca.indexOf(legName);
        this.setState({
            acca: [...this.state.acca.slice(0, legIndex), 
                ...this.state.acca.slice(legIndex + 1)]
        });
    }

    render() {
        let listLegs = this.state.acca
            .map(leg =>
                <LegInputs
                    key={leg.name}
                    name={leg.name}
                    backOdds={leg.backOdds}
                    layOdds={leg.layOdds}
                    commision={leg.commision}
                    handleChange={this.handleInputChange}
                    removeLeg={this.handleRemoveLegClick}
                />
        );
        return (
            <div>
                {listLegs}
                <button onClick={this.handleAddLegClick}>Add leg</button>
            </div>
        );
    }
}

function LegInputs(props) {
    return (
        <div name={props.name}>
            <input type="text" value={props.backOdds}
                name="backOdds" onChange={props.handleChange} />
            <input type="text" value={props.layOdds}
                name="layOdds" onChange={props.handleChange} />
            <input type="text" value={props.commision}
                name="commision" onChange={props.handleChange} />
            {props.name !== "leg1" && 
                <button onClick={props.handleRemoveLegClick}>Remove leg</button>}
        </div>
    );
}

export default App;
