import React from "react";
import Header from "./Header";
import Navigation from "./Navigation";
import Welcome from "./Welcome";
import Calculator from "./Calculator";
import "./App.css";

class App extends React.Component {
    state = {
        betType: "normal",
        backStake: "",
        backOdds: "",
        layOdds: "",
        layCommision: "",
        navigationOpen: false
    }



    handleNavigation = () => {
        this.setState(prevState => ({
            navigationOpen: !prevState.navigationOpen
        }));
    }

    handleInputChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    }

    render() {
        const pushedMainStyle = `${this.state.navigationOpen ? "Pushed" : ""}`;
        return (
            <div className="App">
                <Header handleNavigation={this.handleNavigation}/>
                <Navigation navigationOpen={this.state.navigationOpen}/>
                <main className={`Main-Content ${pushedMainStyle}`}>
                    {Welcome}
                    <Calculator 
                        betType={this.state.betType}
                        backStake={this.state.backStake}
                        backOdds={this.state.backOdds}
                        layOdds={this.state.layOdds}
                        layCommision={this.state.layCommision}
                        handleInputChange={this.handleInputChange}
                    />
                </main>
            </div>
        )
    }
}

export default App;
