import React from "react";
import Calculator from "./Calculator";
import Acca from "./Acca";
import { Switch, Link, Route } from "react-router-dom";

class Tools extends React.Component {
	render() {
		return (
			<div>
				<h1>Choose your calculator</h1>
				<ul>
					<li><Link to="/regular">Regular Calculator</Link></li>
					<li><Link to="/acca">Accumulator Calculator</Link></li>
				</ul>
				<Switch>
					<Route path="/tools/regular" render={(props) => (
						<Calculator {...props}
						    betType={this.state.betType}
						    backStake={this.state.backStake}
						    backOdds={this.state.backOdds}
						    layOdds={this.state.layOdds}
						    layCommision={this.state.layCommision}
						    handleInputChange={this.handleInputChange}
							/>
						)}
					/>
					<Route path="/tools/acca" component={Acca} />
				</Switch>
			</div>
		);
	}
}

export default Tools;
